def simulate_model(trial_data, self_interest_weight=1, fairness_weight=1):
    utility_values = []
    for trial in trial_data:
        split_self = trial.get("split_self", 0)
        token_self = trial.get("token_self", 1) #Default to 1 to avoid division by zero
        split_opp = trial.get("split_opp", 0.0001) #added 0.0001 to avoid division by zero
        token_opp = trial.get("token_opp", 0.0001) #added 0.0001 to avoid division by zero

        if token_self == 0 or token_opp == 0:
          utility = 0 #Handle cases where tokens are 0
        else:
          utility = self_interest_weight * split_self + fairness_weight * ((split_self / token_self) / ((split_opp + 0.0001) / (token_opp + 0.0001)))
        utility_values.append(utility)
    return utility_values
