def simulate_model(trial_data, self_interest_weight=0.5, fairness_weight=0.5):
    utility_values = []
    for trial in trial_data:
        try:
            split_self = trial['split_self']
            combined_earning = trial['combined_earning']
            token_self = trial['token_self']
            token_opp = trial['token_opp']

            #Handle division by zero
            if (token_self + token_opp) == 0:
                fairness_term = 0
            else:
                fairness_term = split_self - (combined_earning * (token_self / (token_self + token_opp)))

            utility = self_interest_weight * split_self + fairness_weight * fairness_term
            utility_values.append(utility)
        except (KeyError, TypeError) as e:
            print(f"Error processing trial {trial}: {e}")
            utility_values.append(float('nan')) # Handle missing data gracefully

    return utility_values
