def simulate_model(trial_data, fairness_weight=1, self_interest_scale=1):
    utility_values = []
    for trial in trial_data:
        try:
            splitperc_self = trial['splitperc_self']
            token_self = trial['token_self']
            token_opp = trial['token_opp']
            split_self = trial['split_self']

            #Handle division by zero
            if token_self + token_opp == 0:
                fairness_component = 0
            else:
                fairness_component = 1 - abs(splitperc_self - (token_self / (token_self + token_opp)) * 100)

            utility = fairness_weight * fairness_component + self_interest_scale * split_self
            utility_values.append(utility)
        except KeyError as e:
            print(f"Warning: Missing key {e} in trial data. Skipping this trial.")
            utility_values.append(None) #Or handle missing data as appropriate.

    return utility_values
