def simulate_model(trial_data, self_interest_weight=1, reference_deviation_sensitivity=1):
    utility_values = []
    for trial in trial_data:
        try:
            split_self = trial['split_self']
            token_self = trial['token_self']
            token_opp = trial['token_opp']
            combined_earning = trial['combined_earning']
            trial_type = trial['trial_type']

            #Handle potential division by zero
            if token_self + token_opp == 0:
                reference_point = 0
            else:
                reference_point = (token_self / (token_self + token_opp)) * combined_earning

            U_accept = self_interest_weight * split_self + reference_deviation_sensitivity * (split_self - reference_point * (1 + (trial_type - 2) * 0.2)) * reference_point
            utility_values.append(U_accept)
        except (KeyError, TypeError) as e:
             print(f"Error processing trial {trial}: {e}")
             utility_values.append(float('nan')) #Handle missing data gracefully

    return utility_values
