def simulate_model(trial_data, intercept=0, alpha=1, beta=1):
    model_predictions = []
    for trial in trial_data:
        token_self = trial.get("token_self", 0)
        token_opp = trial.get("token_opp", 0)
        split_perc_self = trial.get("split_perc_self", 0)

        if (token_self + token_opp) > 0:
            deserved_perc_self = token_self / (token_self + token_opp)
        else:
            deserved_perc_self = 0.5

        utility = intercept + alpha * split_perc_self - beta * max(0, deserved_perc_self - split_perc_self)
        model_predictions.append(utility)
    return model_predictions
