def simulate_model(trial_data, deviation_sensitivity=1.0, self_more_contrib_intercept_shift=0.0):
    model_predictions = []
    for trial in trial_data:
        token_self = trial.get("token_self", 0)
        token_opp = trial.get("token_opp", 0)
        sum_tokens = token_self + token_opp

        if sum_tokens == 0:
            fair_perc_self_calc = 50.0
        else:
            fair_perc_self_calc = (token_self / sum_tokens) * 100.0

        trial_type = trial.get("trial_type", 1)
        if trial_type == 3:
            is_self_more_contrib = 1.0
        else:
            is_self_more_contrib = 0.0

        split_perc_self = trial.get("splitperc_self", 50.0)  # Use splitperc_self from the trial data
        U_accept = deviation_sensitivity * (split_perc_self - fair_perc_self_calc) + self_more_contrib_intercept_shift * is_self_more_contrib

        model_predictions.append(U_accept)
    return model_predictions
