def simulate_model(trial_data, alpha=1, beta=1):
    utility_values = []
    for trial in trial_data:
        try:
            split_self = trial["split_self"]
            split_opp = trial["split_opp"]
            combined_earning = trial["combined_earning"]
            token_self = trial["token_self"]
            token_opp = trial["token_opp"]

            if token_self + token_opp ==0:
                self_perception = 0
            else:
                self_perception = token_self / (token_self + token_opp)

            utility = alpha * (split_self - 0.5 * combined_earning) + beta * (split_opp - self_perception)
            utility_values.append(utility)
        except KeyError as e:
            print(f"Warning: Skipping trial due to missing key: {e}.  Trial data: {trial}")
            utility_values.append(float('nan')) # Handle missing data gracefully.

    return utility_values
