def simulate_model(trial_data, lambda_param=1):
    utility_values = []

    for trial in trial_data:
        token_self = trial.get("token_self", 0)
        token_opp = trial.get("token_opp", 0)
        combined_earning = trial.get("combined_earning", 0)
        split_self = trial.get("split_self", 0)

        # Calculate F
        if (token_self + token_opp) > 0:
            F = (token_self / (token_self + token_opp)) * combined_earning
        else:
            F = 0  # handle the case where both token_self and token_opp are 0

        # Calculate U_accept
        if split_self > 0:
            U_accept = math.log(split_self) - lambda_param * abs(math.log(split_self / F)) if F > 0 else math.log(split_self)
        else:
            U_accept = float('-inf')  # Log(0) is undefined, so we set it to -inf if no split

        utility_values.append(U_accept)

    return utility_values
