    },
    "gamma": {
      "description": "Sensitivity to deviation from the reference point",
      "range": {
        "min": 0,
        "max": 10,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
      "source": "learnable"
    },
    "split_self": {
