      "range": {
        "min": 0,
        "max": "inf",
        "inclusive_min": true,
        "inclusive_max": false
      },
      "learnable": false,
      "source": "data"
    },
    "reference_point": {
      "description": "Reference point for a fair split, influenced by relative token contributions",
      "range": {
        "min": "-inf",
        "max": "inf"
      },
      "learnable": false,
      "source": "calculated"
    },
    "combined_earning": {
      "description": "Total amount of money earned",
      "range": {
        "min": 0,
        "max": "inf",
        "inclusive_min": true,
        "inclusive_max": false
      },
