      "range": {
        "min": -100,
        "max": 100,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
      "source": "learnable"
    },
    "gamma": {
      "description": "Fairness sensitivity parameter modulating the effect of deviation from the fair share",
      "range": {
        "min": 0,
        "max": 20,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
      "source": "learnable"
    },
    "split_self": {
      "description": "The proposed share for the participant (in £)",
      "range": {
