      },
      "learnable": true,
      "source": "learnable"
    },
    "alpha": {
      "description": "Sensitivity to experiencing disadvantageous inequity; the penalty per unit shortfall",
      "range": {
        "min": 0.0,
        "max": 10.0,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
      "source": "learnable"
    },
    "split_self": {
      "description": "The amount of money (£) offered to the participant as per the proposal",
      "range": {
        "min": 0,
        "max": "combined_earning",
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": false,
      "source": "data"
