simulation_code:
def simulate_model(trial_data, beta0=0, beta_exploit=1, beta_explore=1, beta_horizon=0):
    model_predictions = []
    for trial in trial_data:
        if trial.get("forced_choice") == False:
            expected_reward1 = trial.get("expected_reward1", 0)
            expected_reward0 = trial.get("expected_reward0", 0)
            forced_count1 = trial.get("forced_count1", 0)
            forced_count0 = trial.get("forced_count0", 0)
            horizon = trial.get("horizon", 1)

            utility = beta0 + beta_exploit * (expected_reward1 - expected_reward0) - beta_explore * (forced_count1 - forced_count0)
            if horizon == 6:
                utility += beta_horizon

            model_predictions.append(utility)
        else:
            model_predictions.append(None)
    return model_predictions
