def simulate_model(trial_data, beta_exploit=0.0, beta_explore=0.0):
    model_predictions = []
    for trial in trial_data:
        if trial.get('forced_choice') == False:
            expected_reward0 = trial.get("expected_reward0", 0)
            expected_reward1 = trial.get("expected_reward1", 0)
            horizon = trial.get("horizon", 1)
            I_horizon = 1 if horizon == 6 else 0
            U = (beta_exploit + beta_explore * I_horizon) * (expected_reward1 - expected_reward0)
            model_predictions.append(U)
        else:
            model_predictions.append(None)
    return model_predictions
