def simulate_model(trial_data, beta_reward=1.0, beta_info=1.0, intercept=0.0):
    model_predictions = []
    for trial in trial_data:
        if trial.get('forced_choice') == False:
            expected_reward0 = trial.get('expected_reward0', 0)
            expected_reward1 = trial.get('expected_reward1', 0)
            horizon = trial.get('horizon', 1)
            n0 = trial.get('n0', 1)
            n1 = trial.get('n1', 1)

            if n0 == 0:
                n0 = 0.000000000001  # Avoid division by zero
            if n1 == 0:
                n1 = 0.000000000001 # Avoid division by zero

            utility = intercept + beta_reward * (expected_reward1 - expected_reward0) + beta_info * horizon * ((1 / math.sqrt(n1)) - (1 / math.sqrt(n0)))
            model_predictions.append(utility)
        else:
            model_predictions.append(None) # Append None for forced choice trials

    return model_predictions
