def simulate_model(trial_data, b0, b1):
    model_predictions = []
    for trial in trial_data:
        token_opp = trial.get("token_opp", 0)
        token_self = trial.get("token_self", 0)

        if token_opp + token_self ==0: #Handle division by zero
            fairness_diff = 0
        else:
            fairness_diff = (token_opp - token_self) / (token_opp + token_self)

        try:
            proposed_perc = 100 * (1 / (1 + math.exp(-(b0 + b1 * fairness_diff))))
            model_predictions.append(proposed_perc)
        except OverflowError: #Handle potential overflow errors in exp function.
            if -(b0 + b1 * fairness_diff) > 700:
                model_predictions.append(100.0) #Approximation for very large values.
            else:
                model_predictions.append(0.0) #Approximation for very small values.

    return model_predictions
