def simulate_model(trial_data, beta0=0, beta_diff=0):
    model_predictions = []
    for trial in trial_data:
        token_opp = trial.get("token_opp", 0)
        token_self = trial.get("token_self", 0)
        try:
            proposed_perc = 100 / (1 + math.exp(-(beta0 + beta_diff * (token_opp - token_self))))
            model_predictions.append(proposed_perc)
        except OverflowError:
            #Handle potential OverflowError from exp() function.  This happens with very large negative arguments.
            model_predictions.append(100) # Or 0 depending on your preference for the handling of this edge case.
    return model_predictions
