simulation_code:
def simulate_model(trial_data, alpha=0, beta=0):
    model_predictions = []
    for trial in trial_data:
        token_self = trial.get("token_self", 0)
        token_opp = trial.get("token_opp", 0)

        # Handle edge cases to avoid division by zero and log of zero
        if token_self == 0 and token_opp == 0:
            u = 0  # Or some other default value, like alpha
        elif token_self == 0:
            u = float('-inf') # token_self is zero so log(token_opp/token_self) should equal negative infinity
        elif token_opp == 0:
            u = float('inf')  # token_opp is zero so log(token_opp/token_self) should equal infinity
        else:
            u = alpha - beta * ((token_self - token_opp) / (token_self + token_opp)) + math.log(token_opp / token_self)

        proposed_perc = 100 / (1 + math.exp(-u))
        model_predictions.append(proposed_perc)
    return model_predictions
