def simulate_model(trial_data, intercept=50, beta_param=0):
    model_predictions = []
    for trial in trial_data:
        token_opp = trial.get("token_opp", 0)
        token_self = trial.get("token_self", 0)

        if (token_opp + token_self) == 0:
            normalized_diff = 0  # Avoid division by zero if both are zero
        else:
            normalized_diff = (token_opp - token_self) / (token_opp + token_self)

        proposed_perc = intercept + beta_param * normalized_diff
        model_predictions.append(proposed_perc)
    return model_predictions
