def simulate_model(trial_data, b0=0, b1=0):
    model_predictions = []
    for trial in trial_data:
        token_opp = trial.get("token_opp", 0)
        token_self = trial.get("token_self", 0)

        if (token_opp + token_self) == 0:
            normalized_difference = 0  # Avoid division by zero
        else:
            normalized_difference = (token_opp - token_self) / (token_opp + token_self)

        proposed_perc_hat = b0 + b1 * normalized_difference
        model_predictions.append(proposed_perc_hat)
    return model_predictions
