def simulate_model(trial_data, phi=1):
    model_predictions = []
    for trial in trial_data:
        token_opp = trial.get("token_opp", 0)
        token_self = trial.get("token_self", 0)
        if token_opp + token_self ==0:
            U = 0
        else:
            U = phi * ((token_opp - token_self) / (token_opp + token_self))
        try:
            predicted_proposed_perc = 100 / (1 + math.exp(-U))
        except OverflowError:
            if U > 0:
                predicted_proposed_perc = 100
            else:
                predicted_proposed_perc = 0
        model_predictions.append(predicted_proposed_perc)
    return model_predictions
