def simulate_model(trial_data, beta0=0, beta1=0):
    model_predictions = []
    for trial in trial_data:
        split_self = trial.get("split_self", 0)
        token_self = trial.get("token_self", 0)
        token_opp = trial.get("token_opp", 0)
        combined_earning = trial.get("combined_earning", 0)

        # Calculate fair share
        if (token_self + token_opp) == 0:
            fair_share = combined_earning / 2  # Default to 50/50 split if no tokens
        else:
            fair_share = (token_self / (token_self + token_opp)) * combined_earning

        # Calculate p_accept
        utility = beta0 + beta1 * (split_self - fair_share)
        try:
          p_accept = 1 / (1 + math.exp(-utility))
        except OverflowError:
            p_accept = 0.0 if utility < 0 else 1.0

        model_predictions.append(p_accept)
    return model_predictions
