        "inclusive_max": true
      },
      "learnable": false,
      "source": "calculated"
    },
    "lambda_param": {
      "description": "Fairness sensitivity parameter that scales the difference between the offered share and the fair share. A higher value indicates greater sensitivity to fairness deviations.",
      "range": {
        "min": 0,
        "max": 10,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
      "source": "learnable"
    },
    "delta_param": {
      "description": "Additive bias parameter, representing a baseline inclination to accept or reject irrespective of fairness deviation.",
      "range": {
        "min": -10,
        "max": 10,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
      "source": "learnable"
    },
    "split_self": {
      "description": "The monetary share proposed for the participant (responder) in pounds.",
      "range": {
        "min": 0,
        "max": "inf"
      },
      "learnable": false,
      "source": "data"
    },
    "token_self": {
      "description": "The number of tokens found by the participant, representing their contribution.",
      "range": {
        "min": 0,
        "max": "inf"
      },
      "learnable": false,
      "source": "data"
    },
    "token_opp": {
      "description": "The number of tokens found by the opponent, representing the opponent's contribution.",
      "range": {
        "min": 0,
        "max": "inf"
      },
      "learnable": false,
