        "inclusive_max": true
      },
      "learnable": false,
      "source": "data"
    },
    "horizon": {
      "description": "Number of free choices available in the current game (1 or 6)",
      "range": {
        "min": 1,
        "max": 6,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": false,
      "source": "data"
    },
    "n0": {
      "description": "Number of times option 0 has been sampled up to the current trial (forced and past free choices)",
      "range": {
        "min": 1,
        "max": 320,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": false,
      "source": "calculated"
    },
    "n1": {
      "description": "Number of times option 1 has been sampled up to the current trial (forced and past free choices)",
      "range": {
        "min": 1,
        "max": 320,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": false,
      "source": "calculated"
    },
    "beta": {
      "description": "Inverse temperature parameter controlling sensitivity to utility difference",
      "range": {
        "min": 0.001,
        "max": 10,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
      "source": "learnable"
    },
    "gamma": {
      "description": "Exploration bonus weight parameter modulating the influence of uncertainty differences",
      "range": {
        "min": -10,
        "max": 10,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": true,
