        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": false,
      "source": "data"
    },
    "token_self": {
      "description": "Tokens initially found by the responder, measure of contribution",
      "range": { 
         "min": 0, 
         "max": "inf",
         "inclusive_min": true,
         "inclusive_max": false
       },
      "learnable": false,
      "source": "data"
    },
    "token_opp": {
      "description": "Tokens initially found by the opponent, contribution reference",
      "range": { 
        "min": 0, 
        "max": "inf",
        "inclusive_min": true,
        "inclusive_max": false
      },
      "learnable": false,
      "source": "data"
    },
    "I(((token_self / (token_self + token_opp)) - (splitperc_self / 100)) > 0.10)": {
      "description": "Indicator function that equals 1 if the responder receives at least 10% less than proportional fairness based on token contributions, else 0.",
      "range": { 
        "min": 0, 
        "max": 1,
        "inclusive_min": true,
        "inclusive_max": true
      },
      "learnable": false,
      "source": "calculated"
    }
  }
}
