def simulate_model(trials, lambda_neg=0, lambda_pos=0):
    utility_values = []

    for trial in trials:
        split_self = trial.get("split_self", 0)
        token_self = trial.get("token_self", 0)
        token_opp = trial.get("token_opp", 0)
        combined_earning = trial.get("combined_earning", 0)

        # Step 1: Calculate f
        if (token_self + token_opp) > 0:
            f = (token_self / (token_self + token_opp)) * combined_earning
        else:
            f = 0  # Avoid division by zero, define fairness as 0 if no tokens exist

        # Step 2: Calculate D
        if f > 0 and split_self > 0:
            D = math.log(split_self / f)
        else:
            D = float('inf')  # Set to infinity if split_self is 0 or f is 0 (indicating unfairness)

        # Step 3: Calculate U_accept
        if split_self < f:
            penalty = lambda_neg * abs(D)
        else:
            penalty = lambda_pos * abs(D)

        U_accept = split_self - penalty
        utility_values.append(U_accept)

    return utility_values
